class Santa
  def what_floor(instructions)
    @floor = 0
    instructions.chars.each(&method(:read_instruction))
    @floor
  end

  def position_of_basement_instruction(instructions)
    @floor = 0
    instructions.chars.each_with_index do |instr, i|
      read_instruction(instr)
      return i+1 if @floor < 0
    end
  end

  private

  def read_instruction(instr)
    case instr
    when '('
      @floor += 1
    when ')'
      @floor -= 1
    end
  end
end

if defined? DATA
  input = DATA.read
  puts Santa.new.what_floor(input)
  puts Santa.new.position_of_basement_instruction(input)
end

