require_relative '../util'

class Day04 < Grid
  def occupied?(pos)
    self[pos] == '@'
  end

  def accessible?(pos)
    pos.all_8_neighbors.count { occupied?(it) } < 4
  end

  def accessible_positions
    each_position.select { occupied?(it) && accessible?(it) }
  end

  def remove_accessible_rolls!
    accessible_positions.each { self[it] = '.' }
  end

  def remove_recursively_accessible_rolls!
    removed = remove_accessible_rolls!
    if removed.empty?
      []
    else
      removed + remove_recursively_accessible_rolls!
    end
  end

  def part_1
    accessible_positions.size
  end

  def part_2
    dup.remove_recursively_accessible_rolls!.size
  end
end

if defined? DATA
  day = Day04.parse(DATA.read)
  puts day.part_1
  puts day.part_2
end

__END__
